import { useEffect,useState } from 'react';
import axios from "axios"
import Table from 'react-bootstrap/Table';


function Home() {
    
    const [data,setData] = useState();

    const loadData = async () =>{
        const res = await axios.get("http://localhost:5000/getData")
        setData(res.data)
    }

    useEffect (()=>{
        loadData().then(
            console.log("Dati Caricati con successo!!")
        ).catch(
            console.log("Errore!")
        )
    },[])

    return (
      <div className="App">
        <Table striped bordered hover style={{padding: 500}}>
        <thead>
          <tr>
            <th>#</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Username</th>
          </tr>
        </thead>
        <tbody>
          {data.map((d)=>{
            return(
              <>
                  <tr key={data.id}>
                    <td>{d.id}</td>
                    <td colSpan={2}>{d.id_wasp}</td>
                    <td>@twitter</td>
                  </tr>
              </>
            )
          })}
        </tbody>
      </Table>
      </div>
    );
  }
  
  export default Home;
